INSERT INTO `course` (`Dep_Id`, `Num`, `Name`, `Description`, `Credits`, `Attr`, `Professor`) VALUES
(1, '150', 'Biology I', 'First of a two-course sequence, introduction to biochemistry, molecular genetics, cell structure and function, and evolution. Lab required.', 4, 'Breadth-Life Sciences, Experiences-Laboratory, Intro Natural Sciences & Math, Science Lab Course', 'Christopher Theodorakis,	Kelly Barry, Christine Simmons,	Elizabeth Esselman'),
(1, '151', 'Biology II', 'Second of a two-course sequence, introduction to biochemistry, molecular genetics, cell structure and function, and evolution. Lab required.', 4, 'Breadth-Life Sciences, Experiences-Laboratory, Intro Natural Sciences & Math, Science Lab Course', 'Christopher Theodorakis,	Kelly Barry, Christine Simmons,	Elizabeth Esselman'),
(1, '319', 'Cell and Molecular Biology', 'Basic biological chemistry as related to cellular function. Introduction to the structure and function of macromolecule. Differentiation between eukaryotes and prokaryotes. Three lectures and one lab per week.', 0, '', ''),
(4, '131/131L', 'College Physics I', 'This course is the first semester of a two semester sequence. Designed to meet pre-medical and biological science requirements. Topics include mechanics, fluids, energy and heat and gravitation.', 0, '', ''),
(3, '107', 'Concepts of Stats', 'Basic concepts of descriptive statistics; probability distribution and inferential statistics (estimating parameters and testing hypotheses); sampling, experimental design, correlation and regression, consumer price index. Credit may not be granted for both 107 and 244.', 0, '', ''),
(2, '121A', 'General Chemistry I', 'University-level modern chemistry for science students, atomic structure, molecular bonding, structure, stoichiometry, chemical change, equilibrium, qualitative analysis. Four lecture hours per week. Must be taken in sequence.', 0, '', ''),
(2, '121B', 'General Chemistry II', 'University-level modern chemistry for science students, atomic structure, molecular bonding, structure, stoichiometry, chemical change, equilibrium, qualitative analysis. Four lecture hours per week. Must be taken in sequence.', 0, '', ''),
(2, '125A', 'General Chemistry Lab I', 'Laboratory safety practices, techniques, qualitative and quantitative analysis, chemical change and equilibria. One three-hour laboratory per week.', 0, '', ''),
(2, '125B', 'General Chemistry Lab II', 'Laboratory safety practices, techniques, qualitative and quantitative analysis, chemical change and equilibria. One three-hour laboratory per week.', 0, '', ''),
(1, '220', 'Genetics', 'Introduction to transmission, molecular and population genetics with applications to all organisms. Lab required. ', 0, '', ''),
(1, '350', 'Microbiology', 'Structure, metabolism, and genetics of bacteria and bacteriophages. Role of bacteria in disease, biotechnology, and the environment.', 0, '', ''),
(2, '241A', 'Organic Chemistry I', 'Structural types of organic compounds correlated with chemical and physical properties. Bonding, reaction dynamics, reaction types, stereochemistry, functional groups, spectroscopic methods. Three lecture hours per week. ', 0, '', ''),
(3, '244', 'Statistics', 'Summarizing data, including distributions, change and growth, relationships. Basics of survey design and experimental design. Inferential statistics, including confidence intervals and hypothesis testing. Credit may not be granted for both 107 and 244.', 0, '', '');

